import { createFeatureSelector, createSelector } from '@ngrx/store';
export const selectFeature = createFeatureSelector('todos');
export const selectTodos = createSelector(selectFeature, todos => todos.todos);
export const selectFilter = createSelector(selectFeature, t => t.filter);
export const selectPendingTodos = createSelector(selectFeature, state => state.todos.filter(todo => !todo.completion).length);
